import { Dispatch } from "redux";
import { socialMediaAPI } from "../../api/socialMedia";

//initial State
const usersState = {
  users: {
    items: [],
    totalUsersCount: 0,
    error: null,
  },
  pageSize: 6,
  totalUsersCount: 0,
  currentPage: 1,
  paginationCount: [1, 2, 3, 4, 5],
  isFetching: false,
};

//Reducer
const usersReducer = (
  state: UserPageType = usersState,
  action: ActionsType
) => {
  switch (action.type) {
    case "usersPage/followUser":
      return {
        ...state,
        users: {
          ...state.users,
          items: [
            ...state.users.items.map((user) => {
              if (user.id === action.userId) {
                return { ...user, followed: true };
              } else {
                return { ...user };
              }
            }),
          ],
        },
      };
    case "usersPage/unFollowUser":
      return {
        ...state,
        users: {
          ...state.users,
          items: [
            ...state.users.items.map((user) => {
              if (user.id === action.userId) {
                return { ...user, followed: false };
              } else {
                return { ...user };
              }
            }),
          ],
        },
      };
    case "usersPage/setUsers":
      return { ...state, users: action.users };
    case "usersPage/changeCurrentPage":
      return { ...state, currentPage: action.value };
    case "usersPage/setTotalUsersCount":
      return { ...state, totalUsersCount: action.value };
    default:
      return state;
  }
};

//Action Creators
export const followUserAC = (userId: number) => {
  return { type: "usersPage/followUser", userId } as const;
};
export const unfollowUserAC = (userId: number) => {
  return { type: "usersPage/unFollowUser", userId } as const;
};
export const setUsersAC = (users: UsersListType) => {
  return { type: "usersPage/setUsers", users } as const;
};
export const currentPageChengerAC = (value: number) => {
  return { type: "usersPage/changeCurrentPage", value } as const;
};
export const setTotalUsersCount = (value: number) => {
  return { type: "usersPage/setTotalUsersCount", value } as const;
};
export const nextPagesPagination = () => {
  return {type: "usersPage/nextPagesPagination"} as const
}
export const previousPagesPagination = () => {
  return {type: "usersPage/"}
}

//Thunk Creators
export const getUsersTC =
  (currentPage?: number, pageSize?: number) => async (dispatch: Dispatch) => {
    if (currentPage && pageSize) {
      socialMediaAPI
        .getUsers(currentPage, pageSize)
        .then((res) => {
          const data = res.data;
          dispatch(setUsersAC(data));
          dispatch(setTotalUsersCount(data.totalCount));
        })
        .catch((error) => {
          console.log(error.message);
        });
    } else {
      socialMediaAPI.getUsers().then((res) => {
        dispatch(setUsersAC(res.data));
        dispatch(setTotalUsersCount(res.data.totalCount));
      });
    }
  };

export type UserPageType = {
  users: UsersListType;
  pageSize: number;
  totalUsersCount: number;
  currentPage: number;
  paginationCount: number[];
  isFetching: boolean;
};

export type UsersListType = {
  items: UserType[];
  totalUsersCount: number;
  error: string | null;
};

export type UserType = {
  name: string;
  id: number;
  photos: {
    small: string | null;
    large: string | null;
  };
  status: string | null;
  followed: boolean;
};

type ActionsType =
  | ReturnType<typeof followUserAC>
  | ReturnType<typeof unfollowUserAC>
  | ReturnType<typeof setUsersAC>
  | ReturnType<typeof currentPageChengerAC>
  | ReturnType<typeof setTotalUsersCount>;

export default usersReducer;
