import { Dispatch } from "redux";
import { postsAPI } from "../../api/postsAPI";

const initialState: FeedPageStateType = [];

export const feedReducer = (state = initialState, action: ActionType) => {
  switch (action.type) {
    case "feedPage/setPosts":
      return [...state, ...action.data];
    default:
      return state;
  }
};

export const setPostsAC = (data: PostType[]) => {
  return { type: "feedPage/setPosts", data } as const;
};
export const setImageAC = (image: string) => {
  return { type: "feedPage/setImage", image } as const;
};

export const getPostsTC = () => (dispatch: Dispatch) => {
  postsAPI.getPosts().then((res) => {
    dispatch(setPostsAC(res.data));
  });
};
export const getImageTC = () => (dispatch: Dispatch) => {
    postsAPI.getImages()
        .then(res => {
            dispatch(setImageAC(res.data))
        })
}

type ActionType = ReturnType<typeof setPostsAC>;

type FeedPageStateType = PostType[];

type PostType = {
  userId: number;
  id: number;
  title: string;
  body: string;
};
