import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { RootState } from '../../../store/store'
import { UserPageType, UserType, followUserAC } from '../../../store/reducers/usersReducer'
import { Button } from '../../../shared/Button'

const UsersPage = () => {
  const usersPageData = useSelector<RootState, UserPageType>(state => state.usersPage.users)
  const dispatch = useDispatch()

  console.log(usersPageData);


  return (
    <div>{usersPageData.map(user => {
      return (
        <div style={{border: "1px solid black", margin: "10px", padding: "10px"}} key={user.id}>
          <span><img src={user.userPhoto} alt="" /></span>
          <span>{user.firstName}</span>
          <span>{user.lastName}</span>
          <div>
            {user.isFollowed.toString()}
          </div>
          <Button onClick={() => {dispatch(followUserAC(user.id))}}>Follow</Button>
        </div>
      )
    })}</div>
  )
}

export default UsersPage
