import React, { useState, MouseEvent } from "react";
import s from "./profileStatus.module.css";
import { UilCommentAltEdit } from "@iconscout/react-unicons";

const ProfileStatus = () => {
  const [isEditMode, setEditMode] = useState(false);

  const editModeHandler = (e: MouseEvent<SVGElement>) => {
    setEditMode((prev) => prev)
  };

  return isEditMode ? (
    <div className={s.profileStatusWrapper}>
      <UilCommentAltEdit size={40} onClick={editModeHandler} />
      <p>
        Lorem ipsum dolor, sit amet consectetur adipisicing elit. Non ratione
        quibusdam nihil velit quia architecto eum maiores? Non, sequi maxime?
      </p>
    </div>
  ) : (
    <div></div>
  );
};

export default ProfileStatus;
