import React, { useEffect, useState } from "react";
import s from "./header.module.css";
import {
  UilBell,
  UilEnvelope,
  UilHome,
  UilMessage,
  UilNewspaper,
  UilPlay,
  UilSearch,
  UilSetting,
  UilUser,
  UilUsersAlt,
  UilYinYang,
} from "@iconscout/react-unicons";
import miniAva from "../../assets/UserImageSample.jpg";
import { NavLink } from "react-router-dom";
import useSelection from "antd/lib/table/hooks/useSelection";
import { RootState } from "../../store/store";
import { useDispatch, useSelector } from "react-redux";
import { logoutTC, meTC } from "../../store/reducers/authReducer";

export const Header = () => {
  const isAuthorized = useSelector<RootState, boolean>(
    (state) => state.auth.isAuthorized
  );
  const dispatch = useDispatch();

  const [logoutMenu, setLogoutMenu] = useState<boolean>(true)

  useEffect(() => {
    dispatch(meTC());
  }, [dispatch]);

  return (
    <header className={s.headerWrapper}>
      <div className={s.headerContainer}>
        <div className={s.headerLeftSideContainer}>
          <div className={s.headerIconContainer}>
            <UilYinYang size={50} />
          </div>
          <div className={s.headerSearchContainer}>
            <UilSearch />
            <input type="text" placeholder="Search..." />
          </div>
        </div>
        <div className={s.headerNavigation}>
          <ul>
            <li>
              <NavLink to={"/"}>
                <UilHome size={30} />
              </NavLink>
            </li>
            <li>
              <UilNewspaper size={30} />
            </li>
            <li>
              <UilUsersAlt size={30} />
            </li>
            <li>
              <UilPlay size={30} />
            </li>
          </ul>
        </div>
        <div className={s.headerProfileSettings}>
          <ul>
            <li>
              <UilEnvelope />
            </li>
            <li>
              <UilBell />
            </li>
            {isAuthorized ? (
              <li className={s.headerUserImage} onClick={() => setLogoutMenu(prev => !prev)} onBlur={() => setLogoutMenu(false)} tabIndex={0}>
                <img src={miniAva} alt="" />
                {logoutMenu && <div onClick={() => dispatch(logoutTC())}>logout</div>}
              </li>
            ) : (
              <li className={s.headerLoginButton}>
                <NavLink to={"/login"}>
                  <UilUser />
                </NavLink>
              </li>
            )}
          </ul>
        </div>
      </div>
    </header>
  );
};
