const usersState = {
  users: [],
};

export type UserPageType = {
  users: UserType[]
}

export type UserType = {
  id: string;
  userPhoto: string;
  firstName: string;
  lastName: string;
  isFollowed: boolean;
  status: string;
  location: {
    country: string;
    city: string;
  };
};

type ActionsType =
  | ReturnType<typeof followUserAC>
  | ReturnType<typeof unfollowUserAC>
  | ReturnType<typeof setUsersAC>

const usersReducer = (state: UserPageType = usersState, action: ActionsType) => {
  switch (action.type) {
    case "usersPage/followUser":
      return {
        ...state,
        users: state.users.map((user) => {
          if (user.id === action.userId) {
            console.log(user);

            return { ...user, isFollowed: true };
          }
          return {...user};
        }),
      };
    // case "usersPage/unFollowUser":
    //   // return {
    //   //   ...state,
    //   //   users: state.users.map((user) => {
    //   //     if (user.id === action.userId) {
    //   //       return { ...user, isFollowed: false };
    //   //     }
    //   //     return {...user};
    //   //   }),
    //   // };
      case "usersPage/setUsers": 
        return {...state, users: action.users}
    default:
      return state;
  }
};

export const followUserAC = (userId: string) => {
  return { type: "usersPage/followUser", userId} as const;
};
export const unfollowUserAC = (userId: string) => {
  return { type: "usersPage/unFollowUser", userId} as const;
};
export const setUsersAC = (users: UserType[]) => {
  return { type: "usersPage/setUsers", users} as const 
}

export default usersReducer;
