import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { RootState } from '../../../store/store'
import { UserType } from '../../../store/reducers/usersReducer'
import { Button } from '../../../shared/Button'

const UsersPage = () => {
  const usersPageData = useSelector<RootState, Array<UserType>>(state => state.usersPage)
  const dispatch = useDispatch()

  const followUserHandler = (userId: string) => {
    dispatch(userId)
    console.log(usersPageData);

  }

  const usersForRender = usersPageData.map(user => {
    return (
      <div style={{border: "1px solid black", margin: "10px", padding: "10px"}}>
        <span><img src={user.userPhoto} alt="" /></span>
        <span>{user.firstName}</span>
        <span>{user.lastName}</span>
        <div>
          {user.isFollowed.toString()}
        </div>
        <Button onClick={() => followUserHandler(user.id)}>Follow</Button>
      </div>
    )
  })
  return (
    <div>{usersForRender}</div>
  )
}

export default UsersPage
