import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { RootState } from "../../../store/store";
import {
  UserPageType,
  followUserAC,
  setUsersAC,
  unfollowUserAC,
} from "../../../store/reducers/usersReducer";
import { Button } from "../../../shared/Button";
import axios from "axios";

const UsersPage = () => {
  const usersPageData = useSelector<RootState, UserPageType>(
    (state) => state.usersPage
  );
  const dispatch = useDispatch();

  const baseURL = "https://social-network.samuraijs.com/api/1.0"

  const getUsersFromApi = async () => {
    try {
      axios.get(`${baseURL}/users`)
        .then((response) => {
          dispatch(setUsersAC(response))
        })
    }
    catch(error) {
      console.log(error);
    }
  }

  return (
    <div>
      <Button onClick={() => {dispatch(setUsersAC(newUsers))}}>Set users</Button>
      <Button onClick={() => {dispatch(setUsersAC([]))}}>Delete all users</Button>
      <Button onClick={getUsersFromApi}>Get users from Api</Button>
      <div>
        {usersPageData.users.map((user) => {
          return (
            <div
              key={user.id}
              style={{
                display: "inline-block",
                padding: "10px",
                margin: "10px",
                border: "1px solid black",
              }}
            >
              <div>
                <img src={user.userPhoto} alt="" />
              </div>
              <span>{user.firstName}</span>
              <span>{user.lastName}</span>
              <div>
                <Button onClick={() => dispatch(followUserAC(user.id))}>
                  Follow
                </Button>
                <Button onClick={() => dispatch(unfollowUserAC(user.id))}>
                  Unfollow
                </Button>
              </div>
              <div>{user.isFollowed.toString()}</div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default UsersPage;
