import { v1 } from "uuid";

const profilePageState = {};

export type ProfilePageType = {
  profile?: ProfileInfoType;
  posts?: PostType[];
};

type ProfileInfoType = {
  aboutMe: string;
  cotacts: {
    facebook: string;
    website: string;
    vk: string;
    twitter: string;
    instagram: string;
    youtube: string;
    github: string;
    mainLink: string;
  };
  lookingForAJob: boolean;
  lookingForAJobDescription: string;
  fullName: string;
  userId: number;
  photos: {
    small: string;
    large: string;
  };
};

type PostType = {
  postId: string;
  postText: string;
};

type AddPostActionType = ReturnType<typeof addPostAC>;
type SetUserProfileActionType = ReturnType<typeof setUserProfileAC>;

type ActionsType = AddPostActionType | SetUserProfileActionType;

const profileReducer = (
  state: ProfilePageType = profilePageState,
  action: ActionsType
) => {
  switch (action.type) {
    case "profilePage/AddPost":
      const newState = { ...state };
      newState.posts?.unshift(action.newPost);
      return newState;
    case "profilePage/setUserProfile": {
      return
    }
    default:
      return state;
  }
};

export const addPostAC = (postText: string) => {
  return {
    type: "profilePage/AddPost",
    newPost: { postId: v1(), postText },
  } as const;
};
export const setUserProfileAC = (profile: ProfileInfoType) => {
  return { type: "profilePage/setUserProfile", profile } as const;
};

export default profileReducer;
