import React from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";

export const DialogsPage = () => {
  return (
    <DialogsPageWrapper>
      <DialogsListWrapper>
        <DialogsList>
          <DialogItem className={"active"}>
            <NavLink to={"/dialogs/1"}>Danila</NavLink>
          </DialogItem>
          <DialogItem>
            <NavLink to={"/dialogs/2"}>Vasya</NavLink>
          </DialogItem>
          <DialogItem>
            {" "}
            <NavLink to={"/dialogs/3"}>Danila</NavLink>
          </DialogItem>
          <DialogItem>
            {" "}
            <NavLink to={"/dialogs/4"}>Danila</NavLink>
          </DialogItem>
        </DialogsList>
      </DialogsListWrapper>
      <MessengerWrapper>
        <Message>Hi</Message>
        <Message>How are you</Message>
        <Message>Good</Message>
      </MessengerWrapper>
    </DialogsPageWrapper>
  );
};

const DialogsPageWrapper = styled.div`
  width: 100%;
  display: flex;
  flex-direction: row;
  justify-content: start;
  gap: 5rem;
`;

//styling the dialogs(users)
const DialogsListWrapper = styled.div``;
const DialogsList = styled.div``;
const DialogItem = styled.div`
  &.active {
    color: red;
  }
`;
//styling the messages window
const MessengerWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;
const Message = styled.div``;
