import axios from "axios";
import { PostType } from "../store/reducers/feedReducer";
import { v1 } from "uuid";

const postsApiInstance = axios.create({
  baseURL: "https://jsonplaceholder.typicode.com/",
  headers: {},
});

export const postsAPI = {
  getPosts: () => {
    return postsApiInstance.get("posts");
  },
  addPost: (userId: number, postTitle: string, postText: string) => {
    return postsApiInstance.post("posts", {id: v1(), userId, postTitle, postText});
  },
};
