import React, { FC, useState } from "react";
import s from "./feedPage.module.css";
import Post from "../../components/Post/Post";
import { addPostAC } from "../../store/reducers/profileReducer";
import {
  UilArrowRight,
  UilEmoji,
  UilImage,
  UilPlus,
  UilSmile,
  UilUser,
  UilVideo,
} from "@iconscout/react-unicons";
import { SideBar } from "../../components/SideBar/SideBar";
import miniAva from "../../assets/UserImageSample.jpg";
import { useAppSelector } from "../../utils/hooks/useAppSelector";
import { useAppDispatch } from "../../utils/hooks/useAppDispatch";
import FeedPosts from "../../components/FeedPosts/FeedPosts";
import FeedRightSideBar from "../../components/FeedRightSideBar/FeedRightSideBar";

type FeedPageProps = {};

const FeedPage: FC<FeedPageProps> = (props) => {
  const profilePageData = useAppSelector((state) => state.profilePage);
  const dispatch = useAppDispatch();
  const isAuthorized = useAppSelector((state) => state.auth.isAuthorized);

  const [textAreaValue, setTextAreaValue] = useState<string>("");

  const addPost = () => {
    dispatch(addPostAC(textAreaValue));
    setTextAreaValue("");
  };

  return (
    <div className={s.feedPageWrapper}>
      <div className={s.feedPageSideBar}>
        <SideBar />
      </div>

      <FeedPosts />

      <FeedRightSideBar />

    </div>
  );
};

export default FeedPage;
