import React, { FC } from "react";
import "./App.css";
import { SideBar } from "./components/SideBar/SideBar";
import { Route, useParams, withRouter } from "react-router-dom";
import FeedPage from "./pages/FeedPage/FeedPage";
import { DialogsPage } from "./pages/DialogsPage/DialogsPage";
import UsersPage from "./pages/UsersPage/UsersPage";
import Header from "./components/Header/Header";
import { Drawer, IconButton, Divider, List, ListItem, ListItemButton, ListItemIcon, ListItemText, AppBarProps, styled } from "@mui/material";

type AppProps = {};

const App: FC<AppProps> = (props) => {
  return (
    <div className="App">
      <Header />
      <div>
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
          },
        }}
        variant="persistent"
        anchor="left"
        open={open}
      >
        <DrawerHeader>
          <IconButton onClick={handleDrawerClose}>
            {theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
          </IconButton>
        </DrawerHeader>
        <Divider />
        <List>
          {['Inbox', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
            <ListItem key={text} disablePadding>
              <ListItemButton>
                <ListItemIcon>
                  {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
                </ListItemIcon>
                <ListItemText primary={text} />
              </ListItemButton>
            </ListItem>
          ))}
        </List>
        <Divider />
        <List>
          {['All mail', 'Trash', 'Spam'].map((text, index) => (
            <ListItem key={text} disablePadding>
              <ListItemButton>
                <ListItemIcon>
                  {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
                </ListItemIcon>
                <ListItemText primary={text} />
              </ListItemButton>
            </ListItem>
          ))}
        </List>
      </Drawer>
      </div>
      {/* <main>
        <SideBar />
        <div className="Content">
          <Route
            path={"/profile/:userId"}
            render={() => <FeedPage />}
          />
          <Route path={"/dialogs"} render={() => <DialogsPage />} />
          <Route path={"/friends"} render={() => <UsersPage />} />
          {/* <Route path={"/news"} component={NewsPage} /> */}
          {/* <Route path={"/music"} component={MusicPage} /> */}
          {/* <Route path={"/settings"} component={SettingsPage} /> */}
        {/* </div> */}
      {/* </main> */} 
    </div>
  );
};

const AppBar = styled(MuiAppBar, {
  shouldForwardProp: (prop) => prop !== 'open',
})<AppBarProps>(({ theme, open }) => ({
  transition: theme.transitions.create(['margin', 'width'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: `${drawerWidth}px`,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  }),
}));

const DrawerHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  padding: theme.spacing(0, 1),
  // necessary for content to be below app bar
  ...theme.mixins.toolbar,
  justifyContent: 'flex-end',
}));

export default App;
