import React, { FC, useEffect } from 'react';
import s from "./post.module.css"
import { UilComment, UilElipsisDoubleVAlt, UilThumbsDown, UilThumbsUp, UilUser } from '@iconscout/react-unicons';
import { useAppDispatch } from '../../utils/hooks/useAppDispatch';
import { getImageTC } from '../../store/reducers/feedReducer';

type PostProps = {
    postText: string,
    postId: number
}

const Post: FC<PostProps> = (props) => {
    const dispatch = useAppDispatch()

    useEffect(() => {
        dispatch(getImageTC(props.postId))
    }, [props.postId, dispatch])

    const {postText} = props
    return (
        <div className={s.postWrapper}>
            <div className={s.postHeader}>
                <div className={s.postHeaderUserName}>
                    <UilUser />
                    <h3>User Name</h3>
                </div>
                <div className={s.postHeaderOptions}>
                    <UilElipsisDoubleVAlt />
                </div>
            </div>
            <div className={s.postBody}>
                <p>{postText}</p>
            </div>
            <div className={s.postShare}>
                <UilComment />
                <UilThumbsUp />
                <UilThumbsDown />
            </div>
        </div>
    );
};

export default Post;
