const usersState = {
  users: [
    {
      id: "1",
      userPhoto:
        "data:image/jpeg;base64,",
      firstName: "Danila",
      lastName: "Nagov",
      isFollowed: false,
      status: "well",
      location: {
        country: "Russia",
        city: "Kirov",
      },
    },
    {
      id: "2",
      userPhoto:
        "data:image/jpeg;base64,",
      firstName: "Danila",
      lastName: "Nagov",
      isFollowed: false,
      status: "well",
      location: {
        country: "Russia",
        city: "Kirov",
      },
    },
    {
      id: "3",
      userPhoto:
        "data:image/jpeg;base64,",
      firstName: "Danila",
      lastName: "Nagov",
      isFollowed: false,
      status: "well",
      location: {
        country: "Russia",
        city: "Kirov",
      },
    },
  ],
};

export type UserPageType = {
  users: UserType[]
}

export type UserType = {
  id: string;
  userPhoto: string;
  firstName: string;
  lastName: string;
  isFollowed: boolean;
  status: string;
  location: {
    country: string;
    city: string;
  };
};

type ActionsType =
  | ReturnType<typeof followUserAC>
  | ReturnType<typeof unfollowUserAC>;

const usersReducer = (state = usersState, action: ActionsType) => {
  switch (action.type) {
    case "usersPage/followUser":
      return {
        ...state,
        users: state.users.map((user) => {
          if (user.id === action.userId) {
            console.log(user);

            return { ...user, isFollowed: true };
          }
          return {...state};
        }),
      };
    case "usersPage/unFollowUser":
      return {
        ...state,
        users: state.users.map((user) => {
          if (user.id === action.userId) {
            return { ...user, isFollowed: false };
          }
          return {};
        }),
      };
    default:
      return state;
  }
};

export const followUserAC = (userId: string) => {
  return { type: "usersPage/followUser", userId} as const;
};
export const unfollowUserAC = (userId: string) => {
  return { type: "usersPage/unFollowUser", userId} as const;
};

export default usersReducer;
