import { Dispatch } from "redux";
import { postsAPI } from "../../api/postsAPI";

export const imagesForPosts = [
  "https://www.stockvault.net/data/2011/05/31/124348/thumb16.jpg",
  "https://t4.ftcdn.net/jpg/02/94/82/71/360_F_294827120_2LLGq6gOnc9MvpZ0ejyQH1Rzo9YkPzNH.jpg",
  "https://www.stockvault.net/data/2007/03/01/101322/thumb16.jpg",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ_EZ7L-Ehpd731tmz0hklIq_IteOi2tb4R6Q&usqp=CAU",
  "data:image/jpeg;base64,",
  "https://media.istockphoto.com/id/1273682054/photo/car-service-worker-applying-nano-coating-on-a-car-detail.jpg?s=612x612&w=0&k=20&c=w2YoGPnv24lcITfiH7jnTYLAj9xCXUrlbHn33lxcd2U=",
  "https://images.unsplash.com/photo-1492144534655-ae79c964c9d7?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8Mnx8Y2Fyc3xlbnwwfHwwfHx8MA%3D%3D&w=1000&q=80",
  "https://media.istockphoto.com/id/1296158947/photo/portrait-of-creative-trendy-black-african-male-designer-laughing.jpg?s=612x612&w=0&k=20&c=1Ws_LSzWjYvegGxHYQkkgVytdpDcnmK0upJyGOzEPcg=",
  "https://media.istockphoto.com/id/1007763808/photo/portrait-of-handsome-latino-african-man.jpg?s=612x612&w=0&k=20&c=XPL1ukeC99OY8HBfNa_njDujOPf9Xz4yCEOo7O3evU0=",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcShwWm9CLZ5KT61u3erNJ3gnwRcVKO144vBoQ&usqp=CAU",
  "https://images3.memedroid.com/images/UPLOADED217/623342e32efd5.jpeg",
  "https://www.dictionary.com/e/wp-content/uploads/2021/09/20210922_atw_memeStonk_800x800-300x300.png",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQCV0Ql6kOJ4VViF11JRTUwpkUxKw6N8fdRAw&usqp=CAU",
];
const initialState: FeedPageStateType = {
  posts: [],
  images: imagesForPosts,
};

export const feedReducer = (state = initialState, action: ActionType) => {
  switch (action.type) {
    case "feedPage/setPosts":
      return { ...state, posts: [...state.posts, ...action.data] };
    default:
      return state;
  }
};

export const setPostsAC = (data: PostType[]) => {
  return { type: "feedPage/setPosts", data } as const;
};
export const addPostAC = () => {
  return { type: "feedPage/addPost" } as const;
};

//thunks
export const getPostsTC = () => (dispatch: Dispatch) => {
  postsAPI.getPosts().then((res) => {
    dispatch(setPostsAC(res.data));
  });
};
export const addPostTC = (userId: number, postTitle: string, postText: string) => (dispatch: Dispatch) => {
  const postId = Math.floor(Math.random())
  postsAPI.addPost(postId, userId, postTitle, postText)
}

type ActionType = ReturnType<typeof setPostsAC> | ReturnType<typeof addPostAC>;

type FeedPageStateType = {
  posts: PostType[];
  images: string[];
};

export type PostType = {
  userId: number;
  id: number;
  title: string;
  body: string;
};
