import React from "react";
import styled from "styled-components";
import DialogItem from "./DialogItem";
import Message from "./Message";

export const DialogsPage = () => {
    const dialogsData = [
        {id: "1", name: "Danila"},
        {id: "2", name: "Vasya"},
        {id: "3", name: "Ivan"},
        {id: "4", name: "Petya"},
    ]
    const messagesData = [
        {text: "Hi"},
        {text: "How are you"},
        {text: "Great"},
        {text: "And You"},
    ]

  return (
    <DialogsPageWrapper>
      <DialogsListWrapper>
        <DialogsList>
            {dialogsData.map(d => {
                return <DialogItem key={d.id} id={d.id} userName={d.name}/>
            })}
        </DialogsList>
      </DialogsListWrapper>
      <MessengerWrapper>
        {messagesData.map((m, index) => {
            return <Message key={index} messageText={m.text}/>
        })}
      </MessengerWrapper>
    </DialogsPageWrapper>
  );
};

const DialogsPageWrapper = styled.div`
  width: 100%;
  display: flex;
  flex-direction: row;
  justify-content: start;
  gap: 5rem;
`;

const DialogsListWrapper = styled.div``;
const DialogsList = styled.div``;

const MessengerWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;
